SELECT a.id, a.title, a.update_time AS time, a.category_id AS category, a.source_url AS author, i.file_path AS imageUrl, NULL AS readingTime, NULL AS editorsPick, COALESCE(s.savedCount, 0) AS savedCount FROM blog_article a LEFT JOIN ( SELECT article_id, COUNT(*) AS savedCount FROM save_list_article GROUP BY article_id ) s ON a.id = s.article_id LEFT JOIN blog_image i ON a.banner_img_id = i.id WHERE a.is_deleted = 0 AND a.article_status = 'published' AND COALESCE(s.savedCount, 0) > 0 AND a.category_id IN ("1","2","3","4","7")
AND a.id NOT IN
#{id}
ORDER BY savedCount DESC, a.update_time DESC LIMIT #{needed}
